<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class JobDepartment extends Model{    public $table = "department";    protected $primaryKey = 'department_id';    public function JobDepartmentLanguage()    {        return $this->hasMany('App\Models\JobDepartmentLanguage' , 'parent_id', 'department_id');    }    public function Jobs()    {        return $this->hasMany('App\Models\Jobs' , 'departmentId', 'department_id');    }}